mapper.select:
SELECT A.* FROM (SELECT ROW_NUMBER() OVER(ORDER BY E.HIRE_DATE DESC) AS RN, E.*, D.DEPARTMENT_NAME FROM DEPARTMENTS D RIGHT JOIN EMPLOYEES E ON D.DEPARTMENT_ID = E.DEPARTMENT_ID
${column} = TRIM(#{query}) ${column} LIKE '%' || #{query} || '%'
) A WHERE A.RN BETWEEN #{beginRecord} AND #{endRecord}
