mapper.select:
SELECT FILE_ATTACH_NO, PATH, ORIGIN, SAVED, DOWNLOAD_CNT
  FROM FILE_ATTACH
 WHERE FILE_ATTACH_NO = #{fileAttachNo}

SELECT GALLERY_NO, WRITER, TITLE, CONTENT, IP, HIT, CREATED, MODIFIED
  FROM GALLERY
 WHERE GALLERY_NO = #{galleryNo}

SELECT FILE_ATTACH_NO, PATH, ORIGIN, SAVED, DOWNLOAD_CNT
  FROM FILE_ATTACH
 WHERE GALLERY_NO = #{galleryNo}

mapper.update:
UPDATE GALLERY
   SET HIT = HIT + 1
 WHERE GALLERY_NO = #{galleryNo}
